import styled from 'styled-components';
import useStats from "../utils/useStats";

const Container = styled.div`
width: 60%;
margin: 2rem auto;
background-color: #e3e3e3;
display: grid;
`;

const StatGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-gap: 1rem;
  background-color: #e9e9e9;
`;

const StatBlock = styled.div`
  font-size: 1.3rem;
  display: flex;
  align-items: flex-end;
  justify-items: center;
  text-align: center;
  padding: 48px 64px;
  border-radius: 16px;
  background: #e9e9e9;
  box-shadow: 4px 4px 4px 0px #d1d9e6 inset, -4px -4px 4px 0px #ffffff inset;
}
`;

const TitleConfirmed = styled.h3`
  font-size: 1.3em;
  text-align: center;
  color: #fd9644;
`;
const TitleDeaths = styled.h3`
  font-size: 1.3em;
  text-align: center;
  color: #fc5c65;
`;
const TitleRecovered = styled.h3`
  font-size: 1.3em;
  text-align: center;
  color: #26de81;
`;

export default function Stats({ url }) {
    const { stats, loading, error } = useStats(url);
    console.log(stats, loading, error);
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error!</p>;
    return (
        <Container>
            <StatGrid>
                <StatBlock>
                    <TitleConfirmed>Confirmed:</TitleConfirmed>
                    <span>{stats.confirmed.value}</span>
                </StatBlock>
                <StatBlock>
                    <TitleDeaths>Deaths: </TitleDeaths>
                    <span>{stats.deaths.value}</span>
                </StatBlock>
                <StatBlock>
                    <TitleRecovered>Recovered:</TitleRecovered>
                    <span>{stats.recovered.value}</span>
                </StatBlock>
            </StatGrid>
        </Container>
    );
}
