import { useState } from 'react';
import useStats from "../utils/useStats";
import Stats from './Stats';
import styled from 'styled-components';

const Searchbox = styled.div`
box-shadow: -6px -6px 9px #ffffff, 6px 6px 9px rgba(0, 0, 0, 0.4);
max-width: 1000px;
margin: 20px auto 0 auto;

border-radius: 30px;
`;






export default function CountrySelector() {
    const { stats: countries, loading, error } = useStats('https://covid19.mathdro.id/api/countries');
    const [selectedCountry, setSelectedCountry] = useState('IRL');
    if (loading) return <p>Loading...</p>;
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error!</p>;

    return (
        <Searchbox>
            <h4>Currently Showing <h2>{selectedCountry}</h2></h4>
            <select
                onChange={e => {
                    setSelectedCountry(e.target.value);
                }}
            >

                {Object.entries(countries.countries).map(([country, code]) => (
                    <option
                        selected={selectedCountry === countries.iso3[code]}
                        key={code}
                        value={countries.iso3[code]}
                    >
                        {country}
                    </option>
                ))}
            </select>
            <Stats url={`https://covid19.mathdro.id/api/countries/${selectedCountry}`}></Stats>
        </Searchbox>
    );
}
